    const arviointi_hyvä = "hyvä"
    const arviointi_neutraali = "neutraali"
    const arviointi_huono = "huono"

    return (
      <div>
        <Button
          handleClick={this.asetaArvoon(this.state.hyvä + 1, arviointi_hyvä)}
          text="Hyvä"
        />
        <Button
          handleClick={this.asetaArvoon(this.state.hyvä + 1, arviointi_neutraali)}
          text="Neutraali"
        />
        <Button
          handleClick={this.asetaArvoon(this.state.hyvä + 1, arviointi_huono)}
          text="Huono"
        />
