//    OSA 2 2.6 puhelinluettelo osa 1 & OSA 2.7 puhelinluettelo osa 2 &
//    2.8 puhelinluettelo osa 3 & 2.9* puhelinluettelo osa 4
//    & 2.10 puhelinluettelo osa 5

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: [
        { 
          name: 'Bertta Siippa',
          phone: '050-7654321',
          id: '0'
        },
        { 
          name: 'Aapo Kuullos',
          phone: '050-3532666',
          id: '1'
        },        { 
          name: 'Cecilia Milan',
          phone: '040-3555666',
          id: '2'
        },        { 
          name: 'Faarao Muhammed',
          phone: '050-1234567',
          id: '3'
        }
      ],
      newName: '',
      newPhone: '',
      filter: ''
    }
  }

  addNote = (event) => {

    event.preventDefault()

    var onkoNimiOlemassa = true

    this.state.persons.forEach(function(item, index) {
      if(item.name === this.state.newName){
        onkoNimiOlemassa = false
      }
     }.bind(this));

     if(onkoNimiOlemassa) {

      const noteObject = {
        name: this.state.newName,
        phone: this.state.newPhone,
        id: this.state.persons.length + 1
        }

       const persons = this.state.persons.concat(noteObject)

       this.setState({
         persons: persons,
         newName: '',
         newPhone: ''
       })
        alert("Nimi tallennettu");
      }else{
        this.setState({
          newName: '',
          newPhone: ''
        })
        alert("Tämä nimi on jo olemassa! Kirjoita jokin toinen nimi.");
      }

  }

  handleChanges = (event) => {

    console.log("event.target.value", event.target.name)
    console.log("this.state[newName]", this.state["newName"])

    if(event.target.name === "newName"){
      this.setState({ newName: event.target.value })
    }else if (event.target.name === "newPhone") {
      this.setState({ newPhone: event.target.value })
    }else{
      this.setState({ filter: event.target.value })
    }
  }

  toggleVisible = () => {
    this.setState({showAll: !this.state.showAll})
  }

  render() {

    const filtteroi = this.state.persons.filter(person => person.name.startsWith(this.state.filter))
    console.log("handleChanges ", this.handleChanges) 
    return (
      <div>
        <h1>Puhelinluettelo</h1>
        <label>rajaa näytettäviä </label>
          <Rajaa name="filter" value={this.state.filter} onChange={this.handleChanges} persons={this.state.persons}/>
        <h1>Lisää uusi</h1>
        <form onSubmit={this.addNote}>
          <label>nimi: </label><input name="newName" value={this.state.newName} 
            onChange={this.handleChanges}/>
            <br/>
          <label>numero: </label><input name="newPhone" value={this.state.newPhone} 
            onChange={this.handleChanges}/>
            <br/>
          <button type="submit">tallenna</button>
        </form>
        <h1>Numerot</h1>
        <ul>
          {filtteroi.map(person => <Henkilot key={person.id} name={person.name} phone={person.phone}/>)}
        </ul>
      </div>
    )
  }
}

const Rajaa = (props) => {
  return (
    <div>
      <input name={props.name} value={props.value}  onChange={props.onChange}/>

    </div>
  )
}

export default App
