import React, { Component } from 'react';
import './App.css';
import axios from 'axios'


// 2.11 puhelinluettelo osa 6


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: [],
      newNote: '',
      filter: '',
      showAll: true
    }
    console.log('constructor')
  }

  componentDidMount() {
    console.log('did mount')
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log('promise fulfilled')
        this.setState({ persons: response.data })
      })
  }

  filtteroi = () => {

    return this.state.persons.filter(person => person.name.startsWith(this.state.filter))

  }



  handleChanges = (event) => {

    console.log("event.target.value", event.target.name)
    console.log("this.state[newName]", this.state["newName"])

    if(event.target.name === "newName"){
      this.setState({ newName: event.target.value })
    }else if (event.target.name === "newPhone") {
      this.setState({ newPhone: event.target.value })
    }else{
      this.setState({ filter: event.target.value })
    }
  }

  render() {
    console.log('render')
    console.log("filtteroi()", this.filtteroi())
    return (

      <div className="App">
        <label>find countries :</label>
          <Rajaa name="filter" value={this.state.filter} onChange={this.handleChanges} persons={this.state.persons}/>
        <ul>
          {filtteroi.map(person => <Henkilot key={person.id} name={person.name} phone={person.phone}/>)}
        </ul>
      </div>
    );
  }
}

const Henkilot = ({ name, phone }) => {
  return (
    <li>{name} {phone}</li>
  )
}

const Rajaa = (props) => {
  return (
    <div>
      <input name={props.name} value={props.value}  onChange={props.onChange}/>

    </div>
  )
}

export default App;
