import React, { Component } from 'react';
import './App.css';
import axios from 'axios'


// 2.11 puhelinluettelo osa 6


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      persons: [],
      newNote: '',
      showAll: true
    }
    console.log('constructor')
  }

  componentDidMount() {
    console.log('did mount')
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log('promise fulfilled')
        this.setState({ persons: response.data })
      })
  }

  render() {
    console.log('render')

    return (

      <div className="App">
        <ul>
          {this.state.persons.map(person => <Henkilot key={person.id} name={person.name} phone={person.number}/>)}
        </ul>
      </div>
    );
  }
}

const Henkilot = ({ name, phone }) => {
  return (
    <li>{name} {phone}</li>
  )
}

export default App;
